import { useState } from 'react';
import { Container, Row, Col, Form, Button, Alert } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const Profile = () => {
  const { user, login, logout } = useAuth(); // Aggiunto logout per gestire la disconnessione
  const [formData, setFormData] = useState({
    firstName: user?.firstName || '',
    lastName: user?.lastName || '',
    email: user?.email || '',
    currentPassword: '',
    newPassword: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      if (formData.newPassword && formData.newPassword !== formData.confirmPassword) {
        setError('Le password non coincidono');
        return;
      }

      const response = await axios.put(`http://localhost:3100/users/${user._id}`, {
        firstName: formData.firstName,
        lastName: formData.lastName,
        currentPassword: formData.currentPassword,
        newPassword: formData.newPassword
      });

      login(response.data); // Aggiorna i dati dell'utente nel context
      setSuccess('Profilo aggiornato con successo');
      setError('');
      
      // Resetta i campi password
      setFormData(prev => ({
        ...prev,
        currentPassword: '',
        newPassword: '',
        confirmPassword: ''
      }));
    } catch (err) {
      setError(err.response?.data?.message || 'Errore durante aggiornamento del profilo');
      setSuccess('');
    }
  };

  const handleDeleteProfile = async () => {
    if (!window.confirm('Sei sicuro di voler eliminare il tuo profilo?')) {
      return;
    }

    try {
      await axios.delete(`http://localhost:3100/users/${user._id}`);
      logout(); // Disconnette l'utente dopo l'eliminazione
      alert('Profilo eliminato con successo');
    } catch (err) {
      setError(err.response?.data?.message || 'Errore durante l\'eliminazione del profilo');
    }
  };

  return (
    <Container className="mt-4">
      <h2 className="mb-4">Gestisci Profilo</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}
      
      <Row className="justify-content-md-center">
        <Col md={6}>
          <Form onSubmit={handleProfileUpdate}>
            <Form.Group className="mb-3">
              <Form.Label>Nome</Form.Label>
              <Form.Control
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Cognome</Form.Label>
              <Form.Control
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={formData.email}
                disabled
              />
            </Form.Group>

            <h4 className="mt-4">Modifica Password</h4>

            <Form.Group className="mb-3">
              <Form.Label>Password Attuale</Form.Label>
              <Form.Control
                type="password"
                name="currentPassword"
                value={formData.currentPassword}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Nuova Password</Form.Label>
              <Form.Control
                type="password"
                name="newPassword"
                value={formData.newPassword}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Conferma Nuova Password</Form.Label>
              <Form.Control
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
              />
            </Form.Group>

            <Button variant="primary" type="submit" className="w-100">
              Aggiorna Profilo
            </Button>
          </Form>

          <Button
            variant="danger"
            className="w-100 mt-3"
            onClick={handleDeleteProfile}
          >
            Elimina Profilo
          </Button>
        </Col>
      </Row>
    </Container>
  );
};

export default Profile;
